mapper.update:
update tbl_reply set
					 writer = #{writer},
					 content = #{content},
					 regDate = NOW()
where rno = #{rno}
  and bno = #{bno}

mapper.delete:
delete from tbl_reply
where rno = #{rno}
  and bno = ${bno}

mapper.select:
select
	rno, bno, writer, content, regDate
from tbl_reply
where bno = #{bno}
  and rno = #{rno}
